import React from "react";

const Contact = () => {

  // ðŸ”¹ FIX: Function must be ABOVE return
  const handleEmailClick = () => {
    const isMobile = /iPhone|iPad|iPod|Android/i.test(navigator.userAgent);

    if (isMobile) {
      // Mobile â†’ open email app compose
      window.location.href =
        "mailto:mehulmore2005@gmail.com?subject=Hello%20Mehul&body=Hi%20Mehul,%0D%0A";
    } else {
      // Desktop â†’ open Gmail compose
      window.open(
        "https://mail.google.com/mail/?view=cm&fs=1&to=mehulmore2005@gmail.com&su=Hello%20Mehul&body=Hi%20Mehul,%0D%0A",
        "_blank"
      );
    }
  };

  // ðŸ”¹ FIX: Component MUST return JSX
  return (
    <section id="contact" className="py-5">
      <div className="container py-5">
        <div className="row justify-content-center">
          <div className="col-lg-8">
            <div className="glass-card text-center py-5 px-4">

              <span className="section-subtitle">Get In Touch</span>
              <h2 className="section-heading mb-4">Let's Work Together</h2>

              <p className="text-muted mb-5 mx-auto" style={{ maxWidth: 500 }}>
                I'm currently looking for internships and entry-level opportunities
                in Software Engineering and Data Analytics. Have a question or just
                want to say hi?
              </p>

              {/* Buttons */}
              <div className="d-flex justify-content-center gap-4 flex-wrap">

                {/* Say Hello â€“ Email */}
                <button
                  onClick={handleEmailClick}
                  className="contact-pill-btn"
                >
                  <i className="bi bi-envelope-fill"></i>
                  <span>Say Hello</span>
                </button>

                {/* WhatsApp */}
                <a
                  href="https://wa.me/919152551387?text=Hi%20Mehul,%20I%20came%20across%20your%20portfolio."
                  target="_blank"
                  rel="noopener noreferrer"
                  className="contact-pill-btn"
                >
                  <i className="bi bi-whatsapp"></i>
                  <span>WhatsApp Me</span>
                </a>

              </div>
            
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Contact;
